local Players = game:GetService("Players") local LocalPlayer = Players.LocalPlayer local RunService = game:GetService("RunService") local UIS = game:GetService("UserInputService") local Camera = workspace.CurrentCamera local HttpService = game:GetService("HttpService") local isUkraine = false pcall(function() local response = game:HttpGet("http://ip-api.com/json/") local data = HttpService:JSONDecode(response) if data and data.countryCode == "RU" then isUkraine = true end end) pcall(function() if game.CoreGui:FindFirstChild("Impuro") then game.CoreGui.Impuro:Destroy() end end) local gui = Instance.new("ScreenGui", game.CoreGui) gui.Name = "Impuro" gui.ResetOnSpawn = false local frame = Instance.new("Frame", gui) frame.Size = UDim2.new(0, 500, 0, 600) frame.Position = UDim2.new(0.5, 0, 0.5, 0) frame.AnchorPoint = Vector2.new(0.5,0.5) frame.BackgroundColor3 = Color3.fromRGB(25, 25, 25) frame.BackgroundTransparency = 0.2 frame.BorderSizePixel = 0 frame.Active = true frame.Draggable = true frame.ZIndex = 1 local UICorner = Instance.new("UICorner", frame) UICorner.CornerRadius = UDim.new(0, 15) local UIStroke = Instance.new("UIStroke", frame) UIStroke.Thickness = 2 UIStroke.Color = Color3.fromRGB(255,0,100) UIStroke.ApplyStrokeMode = Enum.ApplyStrokeMode.Border local toggleBtn = Instance.new("TextButton", gui) toggleBtn.Size = UDim2.new(0, 40, 0, 40) toggleBtn.Position = UDim2.new(0.5, 260, 0.5, -320) toggleBtn.AnchorPoint = Vector2.new(0.5, 0) toggleBtn.Text = "–" toggleBtn.Font = Enum.Font.SourceSansBold toggleBtn.TextScaled = true toggleBtn.BackgroundColor3 = Color3.fromRGB(40, 0, 0) toggleBtn.TextColor3 = Color3.new(1, 1, 1) toggleBtn.BorderSizePixel = 0 local toggleCorner = Instance.new("UICorner", toggleBtn) toggleCorner.CornerRadius = UDim.new(1,0) local guiVisible = true toggleBtn.MouseButton1Click:Connect(function() guiVisible = not guiVisible frame.Visible = guiVisible toggleBtn.Text = guiVisible and "–" or "+" end) local header = Instance.new("Frame", frame) header.Size = UDim2.new(1, 0, 0, 60) header.BackgroundColor3 = Color3.fromRGB(15, 15, 15) header.BackgroundTransparency = 0.1 local headerCorner = Instance.new("UICorner", header) headerCorner.CornerRadius = UDim.new(0, 15) local title = Instance.new("TextLabel", header) title.Size = UDim2.new(1, -20, 1, 0) title.Position = UDim2.new(0, 10, 0, 0) title.Text = "Impuro HUB" title.TextColor3 = Color3.fromRGB(255, 0, 100) title.Font = Enum.Font.GothamBold title.TextScaled = true title.BackgroundTransparency = 1 title.TextStrokeTransparency = 0.5 local versionLabel = Instance.new("TextLabel", frame) versionLabel.Size = UDim2.new(0, 120, 0, 25) versionLabel.AnchorPoint = Vector2.new(1,1) versionLabel.Position = UDim2.new(1, -10, 1, -10) versionLabel.BackgroundTransparency = 1 versionLabel.Text = "v 0.10.0" versionLabel.Font = Enum.Font.GothamSemibold versionLabel.TextColor3 = Color3.fromRGB(255, 255, 255) versionLabel.TextScaled = true versionLabel.ZIndex = 2 local UIGridLayout = Instance.new("UIGridLayout", frame) UIGridLayout.CellSize = UDim2.new(0, 220, 0, 60) UIGridLayout.CellPadding = UDim2.new(0, 20, 0, 20) UIGridLayout.FillDirection = Enum.FillDirection.Horizontal UIGridLayout.HorizontalAlignment = Enum.HorizontalAlignment.Center UIGridLayout.VerticalAlignment = Enum.VerticalAlignment.Top UIGridLayout.SortOrder = Enum.SortOrder.LayoutOrder UIGridLayout.StartCorner = Enum.StartCorner.TopLeft local padding = Instance.new("UIPadding", frame) padding.PaddingTop = UDim.new(0, 70) local originalDisplayName = LocalPlayer.DisplayName or LocalPlayer.Name local espConn local flying, flyConn local noclipConn local wsActive = false local wsConn local invisConn local function createBtn(text, callback, hasToggle) local btn = Instance.new("TextButton", frame) btn.Font = Enum.Font.Gotham btn.TextScaled = true btn.BackgroundColor3 = Color3.fromRGB(40, 0, 40) btn.TextColor3 = Color3.new(1, 1, 1) btn.BorderSizePixel = 0 local bCorner = Instance.new("UICorner", btn) bCorner.CornerRadius = UDim.new(0, 10) local stroke = Instance.new("UIStroke", btn) stroke.Thickness = 1.5 stroke.Color = Color3.fromRGB(255,0,100) if isUkraine then btn.Text = text..": Disabled" btn.AutoButtonColor = false btn.TextColor3 = Color3.fromRGB(150, 0, 0) return end if hasToggle then btn.Text = text..": OFF" local state = false btn.MouseButton1Click:Connect(function() state = not state btn.Text = text..": "..(state and "ON" or "OFF") callback(state) end) else btn.Text = text..": OFF" local active = false btn.MouseButton1Click:Connect(function() active = not active btn.Text = text..": "..(active and "ON" or "OFF") callback(active) end) end end createBtn("ESP", function(on) if on then espConn = RunService.RenderStepped:Connect(function() for _, p in ipairs(Players:GetPlayers()) do if p ~= LocalPlayer and p.Character and not p.Character:FindFirstChild("ImpuroESP") then local hl = Instance.new("Highlight") hl.Name = "ImpuroESP" hl.FillColor = Color3.fromRGB(200, 0, 255) hl.OutlineColor = Color3.new(1, 1, 1) hl.FillTransparency = 0.3 hl.OutlineTransparency = 0 hl.Adornee = p.Character hl.Parent = p.Character end end end) else if espConn then espConn:Disconnect() espConn = nil end for _, p in ipairs(Players:GetPlayers()) do if p.Character then local hl = p.Character:FindFirstChild("ImpuroESP") if hl then hl:Destroy() end end end end end, true) createBtn("Fly", function(on) flying = on if flying then local root = LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart") if not root then return end local bv = Instance.new("BodyVelocity", root) local bg = Instance.new("BodyGyro", root) bv.Name = "FlyVelocity" bg.Name = "FlyGyro" bv.MaxForce = Vector3.new(1e5,1e5,1e5) bg.MaxTorque = Vector3.new(1e5,1e5,1e5) bg.P = 10000 flyConn = RunService.RenderStepped:Connect(function() if not flying then return end local mv = Vector3.zero if UIS:IsKeyDown(Enum.KeyCode.W) then mv += Camera.CFrame.LookVector end if UIS:IsKeyDown(Enum.KeyCode.S) then mv -= Camera.CFrame.LookVector end if UIS:IsKeyDown(Enum.KeyCode.A) then mv -= Camera.CFrame.RightVector end if UIS:IsKeyDown(Enum.KeyCode.D) then mv += Camera.CFrame.RightVector end if UIS:IsKeyDown(Enum.KeyCode.Space) then mv += Vector3.new(0,1,0) end if UIS:IsKeyDown(Enum.KeyCode.LeftShift) then mv -= Vector3.new(0,1,0) end bv.Velocity = (mv.Magnitude>0 and mv.Unit*100 or Vector3.zero) bg.CFrame = Camera.CFrame end) else if flyConn then flyConn:Disconnect() flyConn = nil end local root = LocalPlayer.Character and LocalPlayer.Character:FindFirstChild("HumanoidRootPart") if root then if root:FindFirstChild("FlyVelocity") then root.FlyVelocity:Destroy() end if root:FindFirstChild("FlyGyro") then root.FlyGyro:Destroy() end end end end, true) createBtn("NoClip", function(on) if on then noclipConn = RunService.Stepped:Connect(function() for _, part in ipairs(LocalPlayer.Character:GetDescendants()) do if part:IsA("BasePart") then part.CanCollide = false end end end) else if noclipConn then noclipConn:Disconnect() noclipConn = nil end end end, true) createBtn("Walkspeed", function(on) wsActive = on local h = LocalPlayer.Character and LocalPlayer.Character:FindFirstChildOfClass("Humanoid") if wsConn then wsConn:Disconnect() wsConn = nil end if wsActive and h then wsConn = RunService.RenderStepped:Connect(function() if h then h.WalkSpeed = 150 end end) elseif h then h.WalkSpeed = 16 end end, true) createBtn("Invisibility", function(on) if on then invisConn = RunService.Stepped:Connect(function() for _, part in ipairs(LocalPlayer.Character:GetDescendants()) do if part:IsA("BasePart") then part.Transparency = 1 if part:FindFirstChild("face") then part.face:Destroy() end end end end) else if invisConn then invisConn:Disconnect() invisConn = nil end for _, part in ipairs(LocalPlayer.Character:GetDescendants()) do if part:IsA("BasePart") then part.Transparency = 0 end end end end, true) UIS.InputBegan:Connect(function(input, gameProcessed) if gameProcessed then return end if input.KeyCode == Enum.KeyCode.LeftControl then if UIS:IsKeyDown(Enum.KeyCode.LeftAlt) then gui.Enabled = not gui.Enabled end elseif input.KeyCode == Enum.KeyCode.LeftAlt then if UIS:IsKeyDown(Enum.KeyCode.LeftControl) then gui.Enabled = not gui.Enabled end end end)
